const progressBarEl=document.getElementById("progress-bar");window.addEventListener("scroll",()=>{let height=document.body.scrollHeight-window.innerHeight;let scrollPosition=document.documentElement.scrollTop;let width=(scrollPosition/height)*100;progressBarEl.style.width=width+"%"});const darkModeToggle=document.getElementById("darkModeToggle");darkModeToggle.addEventListener("change",()=>{const themeColorMeta=document.querySelector('meta[name="theme-color"]');if(darkModeToggle.checked){themeColorMeta.setAttribute("content","#333333")}else{themeColorMeta.setAttribute("content","#f0781f")}});document.addEventListener("DOMContentLoaded",()=>{const navbar=document.querySelector(".navbar");const navbarVertical=document.createElement("div");navbarVertical.classList.add("navbar-vertical");navbarVertical.innerHTML=navbar.innerHTML;document.body.appendChild(navbarVertical);const observer=new IntersectionObserver(([entry])=>{if(!entry.isIntersecting){navbarVertical.style.display="flex"}else{navbarVertical.style.display="none"}},{root:null,threshold:0});observer.observe(navbar)});const navLinks=document.querySelectorAll(".carta-nav a");navLinks.forEach((link)=>{link.addEventListener("click",function(){navLinks.forEach((nav)=>nav.classList.remove("active"));this.classList.add("active")})});document.addEventListener("DOMContentLoaded",()=>{const darkModeToggle=document.getElementById("darkModeToggle");const body=document.body;darkModeToggle.addEventListener("change",()=>{if(darkModeToggle.checked){body.classList.add("dark-mode-active")}else{body.classList.remove("dark-mode-active")}})});document.addEventListener("DOMContentLoaded",()=>{const navLinks=document.querySelectorAll(".carta-nav a");const sections=document.querySelectorAll(".promociones, .brasas, .parrillas, .guarniciones, .bebidas");function showSection(targetId){sections.forEach((section)=>{if(section.id===targetId){section.classList.remove("hidden")}else{section.classList.add("hidden")}})}
navLinks.forEach((link)=>{link.addEventListener("click",(event)=>{event.preventDefault();const targetId=link.getAttribute("href").substring(1);showSection(targetId)})});showSection("promociones")})